    children: editable == false ? _jsx(Text, {
      style: {
        color: 'black',
        fontSize: 14,
        fontWeight: 'bold'
      },
      children: text
    }) : _jsx(TextInput, {
      onChangeText: setTextValue,
      value: textValue,
      placeholder: "Put class here"
    })
  });
};

