import React, {useRef, useState} from 'react';
import { Text, View, Animated, TouchableOpacity, TextInput } from 'react-native';

export default function App() {
  const titleY = useRef(new Animated.Value(0)).current;
  const [viewMode, setViewMode] = useState('view')
  Animated.loop(
    Animated.sequence([
      Animated.timing(titleY, {
        toValue: 10,
        duration: 1000,
        useNativeDriver: true,
      }),
      Animated.timing(titleY, {
        toValue: 0,
        duration: 1000,
        useNativeDriver: true,
      }),
    ])
  ).start();
  const TextBox = ({text}) => {
    <View style={{width: 200, height: 100, borderWidth: 2, borderColor: 'black'}}>
      <Text>{text}</Text>
    </View>
  }
  return (
    <>
      {viewMode == 'view' ?
        <>
          <TouchableOpacity onPress={() => {setViewMode('edit')}} style={{position: 'absolute', top: 10, right: 10, zIndex: 2}}>
            <Text style={{fontWeight: 'bold', fontSize: 20}}>Edit</Text>
          </TouchableOpacity>
          <View style={{alignItems: 'center'}}>
            <Animated.View style={{transform: [{translateY: titleY}], alignItems: 'center'}}>
              <Text style={{fontSize: 30, fontWeight: 'bold'}}>Lynfield College Timetable V2</Text>
              <Text>The new and improved lynfield college class timetable that is up 24/7</Text>
            </Animated.View>
          </View>
        </>
      :
        <View style={{flex: 1, justifyContent: 'center', alignItems: 'center'}}>
          <View style={{flexDirection: 'row'}}>
            <TextBox text="Monday"/>
            <TextBox text="Tuesday"/>
            <TextBox text="Wednesday"/>
            <TextBox text="Thursday"/>
            <TextBox text="Friday"/>
          </View>
        </View>
      }
    </>
  );
}
