import React, {useRef, useState} from 'react';
import { Text, View, Animated, TouchableOpacity, TextInput } from 'react-native';

export default function App() {
  const titleY = useRef(new Animated.Value(0)).current;
  const [viewMode, setViewMode] = useState('edit')
  const [mondayBeforeSchool, setMondayBeforeSchool] = useState('')
  const [tuesdayBeforeSchool, setTuesdayBeforeSchool] = useState('')
  const [wednesdayBeforeSchool, setWednesdayBeforeSchool] = useState('')
  const [thursdayBeforeSchool, setThursdayBeforeSchool] = useState('')
  const [fridayBeforeSchool, setFridayBeforeSchool] = useState('')
  Animated.loop(
    Animated.sequence([
      Animated.timing(titleY, {
        toValue: 10,
        duration: 1000,
        useNativeDriver: true,
      }),
      Animated.timing(titleY, {
        toValue: 0,
        duration: 1000,
        useNativeDriver: true,
      }),
    ])
  ).start();
  return (
    <>
      {viewMode == 'view' ?
        <>
          <TouchableOpacity onPress={() => {setViewMode('edit')}} style={{position: 'absolute', top: 10, right: 10, zIndex: 2}}>
            <Text style={{fontWeight: 'bold', fontSize: 20}}>Edit</Text>
          </TouchableOpacity>
          <View style={{alignItems: 'center'}}>
            <Animated.View style={{transform: [{translateY: titleY}], alignItems: 'center'}}>
              <Text style={{fontSize: 30, fontWeight: 'bold'}}>Lynfield College Timetable V2</Text>
              <Text>The new and improved lynfield college class timetable that is up 24/7</Text>
            </Animated.View>
          </View>
        </>
      :
        <View style={{flex: 1, justifyContent: 'center', alignItems: 'center'}}>
          <View style={{flexDirection: 'row'}}>
            <TextBox editable={false}/>
            <TextBox text="Monday" editable={false}/>
            <TextBox text="Tuesday" editable={false}/>
            <TextBox text="Wednesday" editable={false}/>
            <TextBox text="Thursday" editable={false}/>
            <TextBox text="Friday" editable={false}/>
          </View>
          <View style={{flexDirection: 'row'}}>
            <TextBox text="Before School" editable={false}/>
            <TextBox editable={true} textValue={mondayBeforeSchool} setTextValue={setMondayBeforeSchool}/>
            <TextBox editable={true} textValue={tuesdayBeforeSchool} setTextValue={setTuesdayBeforeSchool}/>
            <TextBox editable={true} textValue={wednesdayBeforeSchool} setTextValue={setWednesdayBeforeSchool}/>
            <TextBox editable={true} textValue={thursdayBeforeSchool} setTextValue={setThursdayBeforeSchool}/>
            <TextBox editable={true} textValue={fridayBeforeSchool} setTextValue={setFridayBeforeSchool}/>
          </View>
        </View>
      }
    </>
  );
}

const TextBox = ({text, editable, textValue, setTextValue}) => {
  return(
    <View style={{width: 200, height: 60, borderWidth: 2, borderColor: 'black', justifyContent: 'center', alignItems: 'center'}}>
      {editable == false ?
        <Text style={{color: 'black', fontSize: 14, fontWeight: 'bold'}}>{text}</Text>
      :
        <TextInput
          onChangeText={setTextValue}
          value={textValue}
          placeholder="Put class here"
        />
      }
    </View>
  );
}
